CREATE TABLE tweets_hochwasser (
	id bigint(19) NOT NULL auto_increment,
	iteration int(10),
	text varchar(500),
	hashtags varchar(150),
	author varchar(50),
	retweets int(10),
	timestamp datetime,
	follower int(10),
	geolocation varchar(200),
	place varchar(200),
	commentRef bigint(19),
	PRIMARY KEY (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
